INSERT INTO `recruitInfo` (`id`, `jobName`, `mansize`, `age`, `record`, `pay`, `workingplace`, `company_id`, `work_content`, `work_demand`, `company_user_name`, `home_phone`, `scene_join_number`, `job_id`, `minpay`, `maxpay`) VALUES
(1, '环保咨询员', 1, '22-50', '本科以上', '5K-10K', '闵行区颛桥镇', 1, '1、主持编写环保咨询报告、环保项目验收、应急预案、排污许可证申请等；\n2、负责制订项目实施工作计划，并具体组织技术实施；\n3、负责环保咨询文件汇编及文字、表格、图件编辑；\n4、负责与项目相关方的技术沟通、协调工作；\n5、负责项目报审、报批文件准备；\n6、配合业务部门做好项目评审的相关组织工作。\n7、全面负责合作单位报告技术审核、沟通、打印工作；\n8、负责项目完成后相关资料收集、整理，并按存档要求及时归档。', '1、环境类、化工类相关专业毕业；\n2、1年以上环评、清洁生产、应急预案等环保咨询编写工作经验，专业知识扎实；\n3、具有较强的书面文字功底，良好的语言表达、沟通能力；\n4、具有吃苦耐劳、积极主动的工作态度，富有进取心及团队意识。', NULL, NULL, 1, 0, 5000, 10000),
(2, '废水处理工程师', 1, '25-50', '本科以上', '10K-15K', '闵行区颛桥镇', 1, '1、按水处理工艺技术要求，正确选择工艺流程并确定工艺设计参数；\n2、独立完成污水的水处理工程的方案设计；\n3、按水处理工程设计规范、标准，完成污水的水处理工程的设计和施工图设计及相关的设计管理工作；\n4、指导、监督现场施工安装并调试验收；\n5、负责对市场、设备采购及工程施工等工作提供技术支持。', '1、 环境工程或给排水专业优先考虑，、2年以上大型污水处理项目工作经验或水厂管理经验；\n2、 能独立完成污水处理工艺流程、图纸设计、施工建设等工程工作，有相关化学实验经验；                                                             3、能解决现场本专业的相关技术问题，具有污水处理的项目规划及筹建经验；\n4、熟练使用AutoCAD等专业软件和office办公软件；                        5、具有二级建造师资质,持有中级以上工程师职称的优先考虑。', NULL, NULL, 1, 0, 10000, 15000),
(3, '废气处理工程师', 1, '25-50', '本科以上', '10K-15K', '闵行区颛桥镇', 1, '1、完成废气（voc、脱硫脱硝）处理项目的需求调查，协助项目经理进行技术交流；\n2、能独立完成废气（voc、脱硫脱硝）处理工艺的设计，管路设计，废气收集设计，所需设备材质的选型；\n3、完成方案制作，并能绘制工艺图及部分施工、电气图；\n4、参与技术交流、参与项目管理与调试等工作；', '1、环境工程、化工等相关专业专科以上学历；\n2、熟悉设计规范及标准，能独立完成废气治理方案编制和相关设计；\n3、2年以上VOC、脱硫脱硝，废气治理工程相关工作经验；\n4、能独立完成废气处理项目的工艺设计和安装、调试、验收等与之相关的技术工作；\n5、熟练绘制CAD图。', NULL, NULL, 1, 0, 10000, 15000),
